import React from "react"
import Autosuggest from 'react-autosuggest';

class LocationCard extends React.Component{

    render(){
    return (
        <div>

            <div className = "location-container">
            {this.props.hasLoaded&&<h3 ><span style = {{color:"#aaaaaa"}} >Around </span>{this.props.selectedLocation}</h3>}

            {Object.keys(this.props.locationCoordinates).map((key,index)=>(
            this.props.hasLoaded&&this.props.selectedLocation!==key&&<h5 key = {key} style={{float:"left"}}className = "location-option" onClick={()=>this.props.handleLocationChange(key) }>{key}</h5>
            ))}
            <div className = "map-toggle" style={{marginTop:16}} onClick={this.props.handleMapToggle}>
                    {this.props.showMap?"hide map ∧":"show map ∨"}</div>
            </div>

            <div style = {{height:0,clear:"both"}}/>
        </div>
    )}
}
export default LocationCard
